mapper.select:
SELECT FROM board WHERE cateId = #{cateId}
AND btitle like CONCAT('%', #{pagingDTO.searchText}, '%') OR bcontent like CONCAT('%', #{pagingDTO.searchText}, '%')
AND userId like CONCAT('%', #{pagingDTO.searchText}, '%')
AND btitle like CONCAT('%', #{pagingDTO.searchText}, '%')
ORDER BY bno DESC LIMIT #{pagingDTO.startRow}, #{pagingDTO.endRow}

SELECT COUNT(*) 
 FROM 
    favorite
 WHERE
   bno = #{bNo}
AND userId=#{userId}

mapper.insert:
INSERT INTO favorite (
      userId,
      bno
   ) VALUES (
      #{userId},
      #{bNo} 
   )

mapper.delete:
DELETE FROM favorite
WHERE 
	bno = #{bNo}
	AND userId=#{userId}
