mapper.select:
SELECT *,
(6371*acos(cos(radians(#{UserVO.lat}))*cos(radians(lat))*cos(radians(lon)
-radians(#{UserVO.lon}))+sin(radians(#{UserVO.lat}))*sin(radians(lat))))
AS distance
FROM board LEFT OUTER JOIN category
ON board.cateId = category.cateId
WHERE
1=0

	or board.cateId = 1 and board.value BETWEEN #{FilterDTO.fvalue1} and
	#{FilterDTO.fvalue2}


	or board.cateId = 2 and board.value BETWEEN #{FilterDTO.fvalue1} and
	#{FilterDTO.fvalue2}


	or board.cateId = 3 and board.value BETWEEN #{FilterDTO.fvalue1} and
	#{FilterDTO.fvalue2}


	or board.cateId = 4 and board.value BETWEEN #{FilterDTO.fvalue1} and
	#{FilterDTO.fvalue2}


	or board.cateId = 5 and board.value BETWEEN #{FilterDTO.fvalue1} and
	#{FilterDTO.fvalue2}


	or board.cateId = 6 and board.value BETWEEN #{FilterDTO.fvalue1} and
	#{FilterDTO.fvalue2}

ORDER BY distance asc, bno desc
limit #{pageMaker.pageStart},#{pageMaker.perPageNum}

SELECT count(*)
FROM board
WHERE
1=0

	or cateId = 1 and value BETWEEN #{fvalue1} and #{fvalue2}


	or cateId = 2 and value BETWEEN #{fvalue1} and #{fvalue2}


	or cateId = 3 and value BETWEEN #{fvalue1} and #{fvalue2}


	or cateId = 4 and value BETWEEN #{fvalue1} and #{fvalue2}


	or cateId = 5 and value BETWEEN #{fvalue1} and #{fvalue2}


	or cateId = 6 and value BETWEEN #{fvalue1} and #{fvalue2}

mapper.update:
INSERT INTO favorite (
bno,
userId
) VALUES (
#{bno},
#{userId}
)

mapper.delete:
DELETE
FROM favorite
WHERE
bno = #{bno}
AND userId=#{userId}
